
public class TestWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=10;
		while (i>1) {
			System.out.println(i);
			i--;
		}
		
		String s[]= {"Java1", "Java2", "Java3"};
		int j=0;
		while(j < s.length) {
			System.out.println(s[j]);
			j++;
		}
	}
	

}
